mapper.select:
SELECT id FROM activity

    enable = 1

        and id = #{cm.id}


        and enable = #{cm.enable}


        and create_by = #{cm.createBy}


        and create_time = #{cm.createTime}


        and update_by = #{cm.updateBy}


        and update_time = #{cm.updateTime}


        and remark = #{cm.remark}


        and sort = #{cm.sort}


        and name = #{cm.name}


        and address = #{cm.address}


        and start_time = #{cm.startTime}


        and end_time = #{cm.endTime}


        and contact_person = #{cm.contactPerson}


        and phone = #{cm.phone}


        and introduction = #{cm.introduction}


        and integral = #{cm.integral}


        and sign_in = #{cm.signIn}


        and sign_qr = #{cm.signQr}


        and signout_qr = #{cm.signoutQr}


        and status = #{cm.status}


        and reason = #{cm.reason}


        and belonging_community = #{cm.belongingCommunity}



        and belonging_unit = #{cm.belongingUnit}


ORDER BY update_time DESC



    enable = 1 and status = 1

        and start_time > #{date}


      and  #{date}  between start_time and end_time


        and end_time < #{date}


ORDER BY update_time DESC

select a.id, a.name as "name",a.create_time as "createTime",'1' as type,a.create_by as "createBy" from activity a
a.enable = 1 and a.status = 3
and a.cid = #{cid}
UNION ALL select d.id, d.content as "name",d.create_time as "createTime",'2' as type,d.create_by as "createBy" from demand d
d.enable = 1 and d.status = 3
and d.cid = #{cid}
UNION ALL select r.id, r.name,r.create_time as "createTime" ,'3' as type,r.create_by as "createBy" from resources r
r.enable = 1 and r.status = 3
and r.cid = #{cid}
UNION ALL select p.id,p.name,p.create_time as "createTime",'4' as type,p.create_by as "create_by" from project p
p.enable = 1 and p.status = 3
and p.belonging_community = #{cid}
ORDER BY createTime DESC
